function App() {
    const [username] = useState('Username');
    const [lists, setLists] = useState([
        { id: 1, name: 'List 1', todos: ['Item 1'], members: ['Member 2', 'Member 3', 'Member 4'], isEditing: false },
        { id: 2, name: 'List 2', todos: [], members: ['Member 1'], isEditing: false },
        { id: 3, name: 'List 3', todos: ['Item A', 'Item B'], members: [], isEditing: false },
    ]);
    const [newListName, setNewListName] = useState('');
    const [selectedListId, setSelectedListId] = useState(null);
    const [isTileView, setIsTileView] = useState(false);

    const addNewList = () => {
        if (newListName.trim()) {
            setLists([
                ...lists,
                { id: Date.now(), name: newListName, todos: [], members: [], isEditing: false },
            ]);
            setNewListName('');
        }
    };

    const deleteList = (listId) => {
        setLists(lists.filter((list) => list.id !== listId));
        if (selectedListId === listId) setSelectedListId(null); // Deselect if deleted list was selected
    };

    const updateTodos = (listId, newTodos) => {
        setLists(lists.map((list) => (list.id === listId ? { ...list, todos: newTodos } : list)));
    };

    const updateMembers = (listId, newMembers) => {
        setLists(lists.map((list) => (list.id === listId ? { ...list, members: newMembers } : list)));
    };

    const toggleEditMode = (listId) => {
        setLists(lists.map((list) => (list.id === listId ? { ...list, isEditing: !list.isEditing } : list)));
    };

    const handleNameChange = (listId, newName) => {
        setLists(lists.map((list) => (list.id === listId ? { ...list, name: newName, isEditing: false } : list)));
    };

    const toggleTileView = () => {
        setIsTileView(!isTileView);
    };

    const selectList = (listId) => {
        setSelectedListId(listId);
        setIsTileView(false);
    };

    const filteredList = lists.find((list) => list.id === selectedListId);

    return (
        <div className="app">
            <Header username={username} onLogoClick={toggleTileView} />
            {isTileView && (
                <div className="tile-view">
                    <h2>All Lists</h2>
                    <div className="tile-container">
                        {lists.map((list) => (
                            <div
                                key={list.id}
                                className="tile"
                                onClick={() => selectList(list.id)}
                            >
                                <h3>{list.name}</h3>
                                <p>{list.todos.length} items</p>
                                <p>{list.members.length} members</p>
                                <button
                                    onClick={(e) => {
                                        e.stopPropagation(); // Prevents triggering the `onClick` of the tile
                                        deleteList(list.id);
                                    }}
                                    className="delete-list-btn"
                                >
                                    Delete
                                </button>
                            </div>
                        ))}
                    </div>
                    <div className="add-list-section">
                        <input
                            type="text"
                            placeholder="Enter new list name"
                            value={newListName}
                            onChange={(e) => setNewListName(e.target.value)}
                            className="new-list-input"
                        />
                        <button onClick={addNewList} className="add-list-btn">
                            Add List
                        </button>
                    </div>
                </div>
            )}
            {!selectedListId && !isTileView ? (
                <div className="add-list-section">
                    <input
                        type="text"
                        placeholder="Enter new list name"
                        value={newListName}
                        onChange={(e) => setNewListName(e.target.value)}
                        className="new-list-input"
                    />
                    <button onClick={addNewList} className="add-list-btn">
                        Add List
                    </button>
                </div>
            ) : selectedListId && !isTileView ? (
                <div className="content">
                    <div key={filteredList.id} className="list-container">
                        <TodoList
                            list={filteredList}
                            onToggleEdit={() => toggleEditMode(filteredList.id)}
                            onNameChange={(newName) => handleNameChange(filteredList.id, newName)}
                            todos={filteredList.todos}
                            setTodos={(newTodos) => updateTodos(filteredList.id, newTodos)}
                        />
                        <MemberList
                            members={filteredList.members}
                            setMembers={(newMembers) => updateMembers(filteredList.id, newMembers)}
                        />
                        <button
                            onClick={() => deleteList(filteredList.id)}
                            className="delete-list-btn"
                        >
                            Delete List
                        </button>
                    </div>
                </div>
            ) : null}
        </div>
    );
}
