import React, { useState } from 'react';
import './TodoList.css';

const TodoList = () => {
    const [items, setItems] = useState([{ text: 'Item 1', completed: false }]);
    const [members, setMembers] = useState(['Member 1']);
    const [newItem, setNewItem] = useState('');
    const [newMember, setNewMember] = useState('');

    const handleAddItem = () => {
        if (newItem.trim()) {
            setItems([...items, { text: newItem, completed: false }]);
            setNewItem('');
        }
    };

    const handleAddMember = () => {
        if (newMember.trim()) {
            setMembers([...members, newMember]);
            setNewMember('');
        }
    };

    const handleToggleComplete = (index) => {
        const updatedItems = [...items];
        updatedItems[index].completed = !updatedItems[index].completed;
        setItems(updatedItems);
    };

    const handleDeleteItem = (index) => {
        setItems(items.filter((_, i) => i !== index));
    };

    const handleDeleteMember = (index) => {
        setMembers(members.filter((_, i) => i !== index));
    };

    return (
        <div className="todo-list">
            <h2>Todo List</h2>
            <div className="add-item-form">
                <input
                    type="text"
                    value={newItem}
                    onChange={(e) => setNewItem(e.target.value)}
                    placeholder="Add new item"
                />
                <button onClick={handleAddItem} className="add-item-btn">Add</button>
            </div>
            <ul>
                {items.map((item, index) => (
                    <li key={index} className="todo-item">
                        <span
                            style={{
                                textDecoration: item.completed ? 'line-through' : 'none',
                                color: item.completed ? '#aaa' : 'inherit',
                            }}
                        >
                            {item.text}
                        </span>
                        <div>
                            <button onClick={() => handleToggleComplete(index)} className="check-btn">✓</button>
                            <button onClick={() => handleDeleteItem(index)} className="delete-btn">✗</button>
                        </div>
                    </li>
                ))}
            </ul>

            <h2>Members</h2>
            <div className="add-item-form">
                <input
                    type="text"
                    value={newMember}
                    onChange={(e) => setNewMember(e.target.value)}
                    placeholder="Add new member"
                />
                <button onClick={handleAddMember} className="add-item-btn">Add</button>
            </div>
            <ul>
                {members.map((member, index) => (
                    <li key={index} className="member-item">
                        <span>{member}</span>
                        <button onClick={() => handleDeleteMember(index)} className="delete-btn">✗</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TodoList;
