// TodoList.js
import React from 'react';
import TodoItem from './TodoItem';
import AddItemForm from './AddItemForm';

function TodoList({ todos, setTodos }) {
    const addItem = (item) => {
        setTodos([...todos, item]);
    };

    const deleteList = () => {
        setTodos([]);
    };

    return (
        <div className="todo-list" style={{ position: 'relative' }}>
            {/* Owner title positioned at top left */}
            <div className="header-section">
                <h1 className="owner-title">Owner</h1>
            </div>

            {/* Rest of the list content */}
            <div className="list-name-section">
                <h3 className="list-name">List Name</h3>
                <button className="edit-btn">✏️</button>
            </div>

            {todos.map((todo, index) => (
                <TodoItem key={index} item={todo} />
            ))}

            <div className="list-buttons">
                <button>Members</button>
                <button>Archive List</button>
                <button onClick={deleteList}>Delete List</button>
            </div>
            <AddItemForm addItem={addItem} />
        </div>
    );
}

export default TodoList;
