import React, { useState } from 'react';
import Header from './components/Header';
import './App.css';

function App() {
    const [username] = useState('Username');
    const [lists, setLists] = useState([
        { id: 1, name: 'List 1', todos: ['Item 1'], members: ['Member 1'], isArchived: false },
        { id: 2, name: 'List 2', todos: ['Item A', 'Item B'], members: ['Member 2', 'Member 3'], isArchived: false },
        { id: 3, name: 'List 3', todos: ['Task X'], members: ['Member 4'], isArchived: false }
    ]);
    const [currentView, setCurrentView] = useState('home'); // 'home' or a list ID
    const [showArchived, setShowArchived] = useState(false);

    const deleteList = (id) => {
        setLists(lists.filter((list) => list.id !== id));
        setCurrentView('home');
    };

    const archiveList = (id) => {
        setLists(
            lists.map((list) => (list.id === id ? { ...list, isArchived: true } : list))
        );
        setCurrentView('home');
    };

    const currentList = lists.find((list) => list.id === currentView);

    const renderListDetails = (list) => (
        <div className="list-details">
            <h2>{list.name}</h2>
            <div className="details-section">
                <div>
                    <h3>Items</h3>
                    <ul>
                        {list.todos.map((todo, index) => (
                            <li key={index}>{todo}</li>
                        ))}
                    </ul>
                </div>
                <div>
                    <h3>Members</h3>
                    <ul>
                        {list.members.map((member, index) => (
                            <li key={index}>{member}</li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="list-actions">
                <button
                    className="delete-list-btn"
                    onClick={() => deleteList(list.id)}
                >
                    Delete List
                </button>
                <button
                    className="archive-list-btn"
                    onClick={() => archiveList(list.id)}
                >
                    Archive List
                </button>
            </div>
        </div>
    );

    return (
        <div className="app">
            <Header username={username} />
            {currentView === 'home' ? (
                <div className="home-view">
                    <div className="add-list-section">
                        <button
                            onClick={() => setShowArchived(!showArchived)}
                            className="filter-archived-btn"
                        >
                            {showArchived ? 'Show All Lists' : 'Show Archived Lists'}
                        </button>
                    </div>
                    <div className="lists-container">
                        {lists
                            .filter((list) => (showArchived ? list.isArchived : !list.isArchived))
                            .map((list) => (
                                <div
                                    key={list.id}
                                    className="list-tile"
                                    onClick={() => setCurrentView(list.id)}
                                >
                                    <h3>{list.name}</h3>
                                    <p>Items: {list.todos.length}</p>
                                    <p>Members: {list.members.length}</p>
                                </div>
                            ))}
                    </div>
                </div>
            ) : (
                currentList && renderListDetails(currentList)
            )}
        </div>
    );
}

export default App;
