import axios from 'axios';

const API_URL = 'http://localhost:3000/api'; // Ensure the port matches your backend

const apiService = {
  getUser: (id) => axios.get(`${API_URL}/user/${id}`),
  createUser: (user) => axios.post(`${API_URL}/user`, user),
  updateUser: (id, user) => axios.put(`${API_URL}/user/${id}`, user),
  deleteUser: (id) => axios.delete(`${API_URL}/user/${id}`),
};

export default apiService;
