import axios from 'axios';

const API_URL = 'http://localhost:3003/api/lists'; // Ensure this matches your backend port

const apiService = {
  getLists: () => axios.get(API_URL),
  createList: (list) => axios.post(API_URL, list),
  updateList: (id, list) => axios.put(`${API_URL}/${id}`, list),
  deleteList: (id) => axios.delete(`${API_URL}/${id}`),
};

export default apiService;
