// TodoList.js
import React, { useState } from 'react';
import TodoItem from './TodoItem';
import AddItemForm from './AddItemForm';

function TodoList({ todos, setTodos }) {
    const [members, setMembers] = useState(['Member 1']); // Initial members list
    const [showMembers, setShowMembers] = useState(false); // Controls member list visibility

    const addItem = (item) => {
        setTodos([...todos, item]);
    };

    const deleteList = () => {
        setTodos([]);
    };

    // Adds a new member to the members list
    const addMember = (member) => {
        if (member.trim()) {
            setMembers([...members, member]);
        }
    };

    // Deletes a member from the members list
    const deleteMember = (index) => {
        setMembers(members.filter((_, i) => i !== index));
    };

    return (
        <div className="todo-list">
            <div className="header-section">
                <h2 className="owner-title">Owner</h2>
                <div className="list-name-section">
                    <h3 className="list-name">List Name</h3>
                    <button className="edit-btn">✏️</button>
                </div>
            </div>

            {/* Todo items */}
            {todos.map((todo, index) => (
                <TodoItem key={index} item={todo} />
            ))}

            {/* Buttons below the Todo list */}
            <div className="list-buttons">
                <button onClick={() => setShowMembers(!showMembers)}>Members</button>
                <button>Archive List</button>
                <button onClick={deleteList}>Delete List</button>
            </div>

            {/* Conditional Members List */}
            {showMembers && (
                <div style={{ marginTop: '10px', backgroundColor: '#444', padding: '10px', borderRadius: '8px' }}>
                    <h3>Members</h3>
                    <ul style={{ listStyleType: 'none', padding: 0 }}>
                        {members.map((member, index) => (
                            <li key={index} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '8px' }}>
                                <span>{member}</span>
                                <button onClick={() => deleteMember(index)} style={{ background: 'none', border: 'none', color: '#f00', cursor: 'pointer' }}>✗</button>
                            </li>
                        ))}
                    </ul>
                    <input
                        type="text"
                        placeholder="Add new member"
                        onKeyDown={(e) => {
                            if (e.key === 'Enter') addMember(e.target.value);
                            e.target.value = ''; // Clear input after adding
                        }}
                        style={{ padding: '8px', borderRadius: '4px', width: '100%', marginTop: '8px' }}
                    />
                </div>
            )}

            {/* Add Item Form */}
            <AddItemForm addItem={addItem} />
        </div>
    );
}

export default TodoList;
