import React, { useState } from 'react';
import Header from './components/Header';

import './App.css';

function App() {
    const [username] = useState('Username');
    const [lists, setLists] = useState([
        { id: 1, name: 'List 1', todos: ['Item 1'], members: ['Member 2', 'Member 3'], isArchived: false }
    ]);
    const [newListName, setNewListName] = useState('');
    const [showArchived, setShowArchived] = useState(false);

    const addNewList = () => {
        if (newListName.trim()) {
            setLists([
                ...lists,
                { id: Date.now(), name: newListName, todos: [], members: [], isArchived: false }
            ]);
            setNewListName('');
        }
    };

    const deleteList = (listId) => {
        setLists(lists.filter(list => list.id !== listId));
    };

    const archiveList = (listId) => {
        setLists(lists.map(list => list.id === listId ? { ...list, isArchived: true } : list));
    };

    const toggleArchivedView = () => {
        setShowArchived(!showArchived);
    };

    return (
        <div className="app">
            <Header username={username} />
            <div className="add-list-section">
                <input
                    type="text"
                    placeholder="Enter new list name"
                    value={newListName}
                    onChange={(e) => setNewListName(e.target.value)}
                    className="new-list-input"
                />
                <button onClick={addNewList} className="add-list-btn">Add List</button>
                <button onClick={toggleArchivedView} className="toggle-archived-btn">
                    {showArchived ? 'Show All Lists' : 'Show Archived Lists'}
                </button>
            </div>
            <div className="content">
                {lists
                    .filter(list => showArchived === list.isArchived)
                    .map(list => (
                        <div key={list.id} className="list-tile">
                            <h3>{list.name}</h3>
                            <button onClick={() => deleteList(list.id)} className="delete-list-btn">
                                Delete List
                            </button>
                            <button onClick={() => archiveList(list.id)} className="archive-list-btn">
                                Archive List
                            </button>
                        </div>
                    ))}
            </div>
        </div>
    );
}

export default App;
